sys_ui_action.script:
current.setValue('state','3');
current.update();

var grMedia = new GlideRecord("x_1593175_raas_media");
grMedia.addQuery("title",current.getValue("title"));
grMedia.addQuery("release_year",current.getValue("release_year"));
grMedia.addQuery("type",current.getValue("type"));
grMedia.addQuery("sys_id","!=",current.getValue("sys_id"));
grMedia.query();
grMedia.addEncodedQuery("title="+current.getValue("title")+"^type="+current.getValue("type")+"^release_year="+current.getValue("release_year")+"^sys_id"+"!="+current.getValue("sys_id"));
while(grMedia._next()){
	grMedia.setValue("state","7");
	grMedia.setValue("genre","");
	grMedia.setValue("release_date","");
	grMedia.setValue("category","");
	grMedia.setValue("synopsis","");
	grMedia.setValue("writers","");
	grMedia.setValue("poster","");
	grMedia.setValue("production","");
	grMedia.setValue("website","");
	grMedia.setValue("average_rating","0");
	grMedia.setValue("metascore","0");
	grMedia.setValue("imdb_rating","0");
	grMedia.setValue("awards","");
	grMedia.setValue("country","");
	grMedia.setValue("language","");
	grMedia.setValue("integration_state","1");
	grMedia.setValue("integration_error_message","");

	var grMediaActor = new GlideRecord("x_1593175_raas_m2m_actor_media");
	grMediaActor.addEncodedQuery("x_1593175_raas_media="+ grMedia.getValue("sys_id"));
	grMediaActor.query();
	grMediaActor.deleteMultiple();

	var grMediaDirector = new GlideRecord("x_1593175_raas_m2m_director_media");
	grMediaDirector.addEncodedQuery("x_1593175_raas_media="+ grMedia.getValue("sys_id"));
	grMediaDirector.query();
	grMediaDirector.deleteMultiple();

	grMedia.update();

}

action.setRedirectURL(current);
